package Ex0712;

public class Ex03 {
    public static void main(String[] args) {

        String a = "";
        String b = "java";
        String c = "system";

        System.out.println(a.length());
        System.out.println(b.length());
        System.out.println(c.length());
        System.out.println("모든 문자열 길이= " +(a.length()+b.length()+c.length()));

        String[] str = new String[3];
        str[0] = "a";
        str[1] = "b";
        str[2] = "c";
        //문자열 배열
        //배열의 길이는 length <<
        //스트링의 길이는 length()
        System.out.println("안녕"+str[0].length()+str[1].length()+str[2].length());
    int count = 0;
        for (int i = 0 ; i <str.length ;i++){
            System.out.println(i);
            System.out.println(str[1]);
            System.out.println(str[1].length());
            count = count + str[1].length();
        }
        System.out.println("모든 문자열 길이는 "+count);
    }
}
